@font-face {
  font-family: "my_font5";
  src: url("https://raw.githubusercontent.com/MANOJ-M-01/Assets/main/fonts/FIGHTER%20BRUSH.ttf%22");
}
@font-face {
  font-family: "my_font6";
  src: url("https://raw.githubusercontent.com/MANOJ-M-01/Assets/main/fonts/Fusterd%20Brush.otf%22");
}
body {
  background-color: #0e0c0c;
  height: 100vh;
}

fieldset {
  border: none;
  outline: none;
  border-radius: 5px;
  transition: all 0.35s ease-in-out;
}

fieldset:hover {
  cursor: pointer;
  transform: scale(1.03);
}

.text_head {
  background: linear-gradient(to right, #5E2A73 0%, #bd0835 40%, #bd4a08 60%, #f0a608 90%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  font-family: my_font6;
  font-weight: 100;
  margin: 0;
  line-height: 50px;
  transform: rotateZ(-15deg);
  margin-bottom: 30px;
}

.text {
  background: linear-gradient(to right, #bd4a08 0%, #bd0835 40%, #f0a608 60%, #5E2A73 90%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  font-family: my_font5;
  font-size: 2em;
  font-weight: 100;
  margin: 0;
}

.app_lists ul {
  display: flex;
  justify-content: space-around;
  flex-wrap: wrap;
  list-style-type: none;
  padding: 0;
}
.app_lists ul li .card {
  width: 300px;
  height: 400px;
  margin-bottom: 30px;
  border: 1px solid #07e607;
}

body {
  background-color: linear-gradient(to right, #000000, #0f9b0f);
}

