*{
    padding: 0;
    margin: 0;
    box-sizing: border-box;
}
/* ---------------------------------------------------------------------------------------------------------- */
$colors: (
 WhiteColor: white,
 fontBlackColor: black,
 naviBlue: #2B1B92,
 darkBlue: #0F054E
);

$font:(
 regular: 16px,
 title:24px
);

body, html{
 width: 100%;
}

.contenu{
    padding-top: 55px;
}
 nav{
  background-color: map-get($colors, darkBlue);
  display: flex;
  justify-content: space-evenly;
  
  position: fixed;
  z-index: 1;
  width: 100%;
  height: 50px;
  overflow-x: hidden;

  a{
   align-self: center;
   text-decoration: none;
   color: map-get($colors , WhiteColor);
   padding: 15px;
   line-height: 8px;
   text-align: center;
   font-size: map-get($font, regular);

   &:hover{
    cursor: pointer;
    background-color: map-get($colors , naviBlue);
    border-radius: 5px;
   }
  }
 }


.navv{
    width: 100%;
    height: auto;
    background-color: crimson;
    z-index: 1;
    color: #040714; 
    position: fixed;
    display: flex;
    flex-wrap: wrap;
    margin: auto;
    justify-content: space-around;
    label{
        padding: 10px;
        text-transform: uppercase;
    }
}
.popup{
    height: auto;
    width: 80%;
    margin: auto;
    background-color: #040714;
    position: fixed;
    color: aliceblue;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    display: flex;
    filter: drop-shadow(15px 15px 120px #040714);
    .ul_popup{
        list-style: none;
        padding: 20px;
        .li_popup{
            padding: 9px;
